import React from 'react';
import '../styles/MethodSelector.css';

const MethodSelector = ({ selectedMethod, onMethodChange, methods }) => {
  return (
    <div className="method-selector">
      <label>Chọn phương pháp phát hiện:</label>
      <select
        value={selectedMethod}
        onChange={(e) => onMethodChange(e.target.value)}
        className="method-dropdown"
      >
        {methods.map((method) => (
          <option key={method.code} value={method.code}>
            {method.name}
          </option>
        ))}
      </select>

      {methods.find(m => m.code === selectedMethod)?.description && (
        <div className="method-description">
          <p>{methods.find(m => m.code === selectedMethod).description}</p>
        </div>
      )}
    </div>
  );
};

export default MethodSelector;
