import React from 'react';

const MethodSelector = ({ method, setMethod }) => {
    const descriptions = {
        'frame-based': 'Phương pháp này phân tích từng khung hình trong video để xác định độ tin cậy của chúng.',
        'video-based': 'Phương pháp này xem xét toàn bộ video như một thể thống nhất để đưa ra dự đoán.'
    };

    return (
        <div className="bg-white p-8 rounded-2xl shadow-xl border border-gray-200 transition-all duration-300 hover:scale-[1.01] hover:shadow-2xl">
            <h2 className="text-3xl font-bold mb-6 text-gray-800">Chọn Phương pháp Phân tích</h2>
            <div className="relative mb-6">
                <label htmlFor="analysis-method" className="block text-lg font-semibold text-gray-700 mb-2">
                    Phương pháp
                </label>
                <div className="relative">
                    <select
                        id="analysis-method"
                        className="w-full p-4 border border-gray-300 rounded-lg text-lg bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300 appearance-none"
                        value={method}
                        onChange={(e) => setMethod(e.target.value)}
                    >
                        <option value="frame-based">Phân tích từng khung hình (Frame-based)</option>
                        <option value="video-based">Phân tích toàn bộ video (Video-based)</option>
                    </select>
                    <div className="absolute inset-y-0 right-0 flex items-center px-4 pointer-events-none text-gray-400">
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                        </svg>
                    </div>
                </div>
            </div>
            <div className="bg-gray-50 p-6 rounded-xl border-l-4 border-blue-500 transition-all duration-300">
                <p className="text-gray-600 italic leading-relaxed">
                    {descriptions[method]}
                </p>
            </div>
        </div>
    );
};

export default MethodSelector;
