import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import UploadForm from './components/UploadForm';
import MethodSelector from './components/MethodSelector';
import ResultDisplay from './components/ResultDisplay';
import LoadingSpinner from './components/LoadingSpinner';
import Footer from './components/Footer';
import { detectDeepfake, getAvailableMethods } from './services/api';
import './App.css';

function App() {
  const [result, setResult] = useState(null);
  const [processingTime, setProcessingTime] = useState(0);
  const [isProcessing, setIsProcessing] = useState(false);
  const [selectedMethod, setSelectedMethod] = useState('A');
  const [methods, setMethods] = useState([]);
  const [error, setError] = useState('');

  // Load available methods on startup
  useEffect(() => {
    const loadMethods = async () => {
      try {
        const availableMethods = await getAvailableMethods();
        setMethods(availableMethods);
      } catch (err) {
        console.error('Failed to load methods:', err);
        setMethods([
          { code: 'A', name: 'Frame-wise Baseline', description: 'Phương pháp cơ bản' },
          { code: 'B', name: 'Frame CNN + Voting', description: 'Kết hợp voting' },
          { code: 'C', name: 'Feature Aggregation', description: 'Trích xuất đặc trưng' },
          { code: 'D', name: 'Patch-level + Temporal', description: 'Mô hình thời gian' }
        ]);
      }
    };
    loadMethods();
  }, []);

  const handleUpload = async (file) => {
    setIsProcessing(true);
    setError('');
    const startTime = new Date().getTime();

    try {
      const result = await detectDeepfake(file, selectedMethod);
      setResult(result);

      const endTime = new Date().getTime();
      setProcessingTime(((endTime - startTime) / 1000).toFixed(2));
    } catch (error) {
      console.error('Error:', error);
      setError(error.response?.data?.error || 'Có lỗi xảy ra khi xử lý video');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="app">
      <Header />
      <main className="main-content">
        <div className="container">
          <MethodSelector
            selectedMethod={selectedMethod}
            onMethodChange={setSelectedMethod}
            methods={methods}
          />

          <UploadForm
            onUpload={handleUpload}
            isProcessing={isProcessing}
          />

          {error && <div className="error-message">{error}</div>}

          {isProcessing && <LoadingSpinner />}

          <ResultDisplay
            result={result}
            processingTime={processingTime}
          />
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default App;
