// Result
const ResultDisplay = ({ result, processingTime }) => {
  if (!result) return null;
  const isFake = result.is_fake;
  const confidence = (result.confidence * 100).toFixed(2);

  return (
    <div
      className={`p-6 mt-8 rounded-xl border-l-4 font-semibold text-lg shadow-md ${
        isFake ? 'bg-red-50 border-red-600 text-red-800' : 'bg-green-50 border-green-600 text-green-800'
      }`}
    >
      <h2 className="text-2xl font-bold mb-4">K·∫øt qu·∫£ ph√¢n t√≠ch</h2>
      <div className="text-center">
        <h3 className="text-3xl font-bold mb-2">{isFake ? 'VIDEO GI·∫¢ M·∫†O' : '‚úÖ VIDEO TH·∫¨T'}</h3>
        <p className="text-lg mt-2">ƒê·ªô tin c·∫≠y: {confidence}%</p>
        {processingTime && <p className="text-sm mt-1 opacity-80">‚è±Ô∏è Th·ªùi gian x·ª≠ l√Ω: {processingTime} gi√¢y</p>}
      </div>
