};

// App
const App = () => {
  const [method, setMethod] = useState('method-a'); // ‚úÖ fix
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [processingTime, setProcessingTime] = useState(0);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setResult(null);
    setError(null);
    setProcessingTime(0);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);
    setError(null);
    const startTime = Date.now();

    await new Promise((resolve) => setTimeout(resolve, 3000));

    try {
      const isFake = Math.random() > 0.5;
      const confidence = Math.random();
      setResult({ is_fake: isFake, confidence });
      const endTime = Date.now();
      setProcessingTime(((endTime - startTime) / 1000).toFixed(2));
    } catch {
      setError('ƒê√£ x·∫£y ra l·ªói trong qu√° tr√¨nh ph√¢n t√≠ch.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100 font-inter">
      <Header />
      <main className="flex-1 py-12 px-4 flex justify-center items-start">
        <div className="container mx-auto max-w-5xl">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <MethodSelector method={method} setMethod={setMethod} />
            <UploadForm file={file} loading={loading} handleFileChange={handleFileChange} handleSubmit={handleSubmit} error={error} />
          </div>
          {loading && <LoadingSpinner />}
          {error && <div className="mt-8 p-6 rounded-xl bg-red-50 border-l-4 border-red-600 text-red-800 font-semibold shadow-md">{error}</div>}
          {result && <ResultDisplay result={result} processingTime={processingTime} />}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default App;
