import React, { useState } from 'react';
import Header from './components/Header';
import MethodSelector from './components/MethodSelector';
import UploadForm from './components/UploadForm';

const App = () => {
    const [method, setMethod] = useState('frame-based');
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setResult(null);
        setError(null);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setResult(null);
        setError(null);

        // Mô phỏng quá trình phân tích
        await new Promise(resolve => setTimeout(resolve, 3000));

        try {
            // Thay thế bằng logic gọi API thực tế của bạn
            const isFake = Math.random() > 0.5;
            const confidence = Math.random();
            setResult({ is_fake: isFake, confidence });
        } catch (err) {
            setError('Đã xảy ra lỗi trong quá trình phân tích. Vui lòng thử lại.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-gray-100">
            <Header />
            <main className="flex-1 py-12 px-4 flex justify-center items-center">
                <div className="container mx-auto">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                        <MethodSelector method={method} setMethod={setMethod} />
                        <UploadForm
                            file={file}
                            loading={loading}
                            result={result}
                            error={error}
                            handleFileChange={handleFileChange}
                            handleSubmit={handleSubmit}
                        />
                    </div>
                </div>
            </main>
        </div>
    );
};

export default App;
