import React, { useState } from 'react';
import './index.css';

// Bắt đầu với các component con được định nghĩa trong một file duy nhất
const Header = () => {
    return (
        <header className="bg-white border-b border-gray-200 py-8 shadow-sm">
            <div className="container mx-auto px-4 text-center">
                <h1 className="text-4xl sm:text-5xl font-extrabold tracking-tight leading-tight text-gray-900">
                    Hệ thống Phát hiện Deepfake
                </h1>
                <p className="mt-4 text-lg text-gray-500 max-w-2xl mx-auto">
                    Sử dụng trí tuệ nhân tạo để phân tích video và phát hiện nội dung giả mạo.
                </p>
            </div>
        </header>
    );
};

const Footer = () => {
    return (
        <footer className="bg-gray-800 text-white py-6 mt-12 text-center">
            <div className="container mx-auto px-4">
                <p className="text-sm opacity-80">Đồ án nghiên cứu phát hiện Deepfake Video</p>
                <p className="text-sm mt-1 opacity-80">© Nguyễn Dương Minh Quan - 2251012119</p>
            </div>
        </footer>
    );
};

const MethodSelector = ({ method, setMethod }) => {
    const descriptions = {
        'frame-based': 'Phương pháp này phân tích từng khung hình trong video để xác định độ tin cậy của chúng.',
        'video-based': 'Phương pháp này xem xét toàn bộ video như một thể thống nhất để đưa ra dự đoán.'
    };

    return (
        <div className="bg-white p-8 rounded-2xl shadow-xl border border-gray-200 transition-all duration-300 hover:scale-[1.01] hover:shadow-2xl">
            <h2 className="text-3xl font-bold mb-6 text-gray-800">Chọn Phương pháp Phân tích</h2>
            <div className="relative mb-6">
                <label htmlFor="analysis-method" className="block text-lg font-semibold text-gray-700 mb-2">
                    Phương pháp
                </label>
                <div className="relative">
                    <select
                        id="analysis-method"
                        className="w-full p-4 border border-gray-300 rounded-lg text-lg bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300 appearance-none"
                        value={method}
                        onChange={(e) => setMethod(e.target.value)}
                    >
                        <option value="frame-based">Phân tích từng khung hình (Frame-based)</option>
                        <option value="video-based">Phân tích toàn bộ video (Video-based)</option>
                    </select>
                    <div className="absolute inset-y-0 right-0 flex items-center px-4 pointer-events-none text-gray-400">
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                        </svg>
                    </div>
                </div>
            </div>
            <div className="bg-gray-50 p-6 rounded-xl border-l-4 border-blue-500 transition-all duration-300">
                <p className="text-gray-600 italic leading-relaxed">
                    {descriptions[method]}
                </p>
            </div>
        </div>
    );
};

const UploadForm = ({
    file,
    loading,
    handleSubmit,
    handleFileChange,
    error
}) => {
    return (
        <div className="bg-white p-8 rounded-2xl shadow-xl border border-gray-200 transition-all duration-300 hover:scale-[1.01] hover:shadow-2xl">
            <h2 className="text-3xl font-bold mb-6 text-gray-800">Tải Video lên</h2>
            <form onSubmit={handleSubmit} className="space-y-6">
                <div className="form-group">
                    <label
                        htmlFor="video-file"
                        className="flex flex-col items-center justify-center p-8 border-2 border-dashed border-gray-300 rounded-xl bg-gray-50 text-gray-500 cursor-pointer transition-all duration-300 hover:border-blue-500 hover:bg-gray-100"
                    >
                        <svg className="w-16 h-16 mb-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12"></path>
                        </svg>
                        <span className="font-semibold text-gray-700">Kéo và thả video vào đây</span>
                        <span className="text-sm">hoặc nhấp để chọn tệp</span>
                        <input
                            type="file"
                            id="video-file"
                            className="hidden"
                            accept="video/*"
                            onChange={handleFileChange}
                        />
                    </label>
                </div>

                {file && (
                    <div className="text-gray-600 text-center text-sm transition-all duration-300 transform scale-y-100 h-auto opacity-100 origin-top">
                        Đã chọn tệp: <strong>{file.name}</strong> ({ (file.size / 1024 / 1024).toFixed(2) } MB)
                    </div>
                )}

                <button
                    type="submit"
                    className={`
                        w-full py-4 px-6 rounded-xl font-bold text-lg uppercase tracking-wide
                        transition-all duration-300
                        ${loading || !file
                            ? 'bg-gray-400 cursor-not-allowed'
                            : 'bg-gradient-to-r from-blue-600 to-blue-800 text-white shadow-lg hover:shadow-xl hover:scale-105'
                        }
                    `}
                    disabled={loading || !file}
                >
                    {loading ? (
                        <span className="flex items-center justify-center space-x-2">
                            <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                            </svg>
                            <span>Đang phân tích...</span>
                        </span>
                    ) : (
                        <span>Phân tích</span>
                    )}
                </button>
            </form>
        </div>
    );
};

const LoadingSpinner = () => {
    return (
        <div className="text-center p-8 my-4">
            <div className="w-16 h-16 border-4 border-gray-200 border-t-blue-500 rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-gray-600 font-medium">Đang xử lý video...</p>
        </div>
    );
};

const ResultDisplay = ({ result, processingTime }) => {
    if (!result) return null;

    const isFake = result.is_fake;
    const confidence = (result.confidence * 100).toFixed(2);
    const borderColor = isFake ? 'border-red-600' : 'border-green-600';
    const bgColor = isFake ? 'bg-red-50' : 'bg-green-50';
    const textColor = isFake ? 'text-red-800' : 'text-green-800';

    return (
        <div className={`p-6 mt-8 rounded-xl border-l-4 font-semibold text-lg ${bgColor} ${borderColor} ${textColor} shadow-md`}>
            <h2 className="text-2xl font-bold mb-4">Kết quả phân tích</h2>
            <div className="text-center">
                <h3 className="text-3xl font-bold mb-2">
                    {isFake ? '⚠️ VIDEO GIẢ MẠO' : '✅ VIDEO THẬT'}
                </h3>
                <p className="text-lg mt-2">Độ tin cậy: {confidence}%</p>
                {processingTime && (
                    <p className="text-sm mt-1 opacity-80">Thời gian xử lý: {processingTime} giây</p>
                )}
            </div>
        </div>
    );
};

// Component chính App
const App = () => {
    const [method, setMethod] = useState('frame-based');
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);
    const [processingTime, setProcessingTime] = useState(0);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setResult(null);
        setError(null);
        setProcessingTime(0);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setResult(null);
        setError(null);
        const startTime = Date.now();

        // Mô phỏng quá trình phân tích
        await new Promise(resolve => setTimeout(resolve, 3000));

        try {
            // Thay thế bằng logic gọi API thực tế của bạn
            const isFake = Math.random() > 0.5;
            const confidence = Math.random();
            setResult({ is_fake: isFake, confidence });
            const endTime = Date.now();
            setProcessingTime(((endTime - startTime) / 1000).toFixed(2));
        } catch (err) {
            setError('Đã xảy ra lỗi trong quá trình phân tích. Vui lòng thử lại.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-gray-100 font-inter">
            <Header />
            <main className="flex-1 py-12 px-4 flex justify-center items-start">
                <div className="container mx-auto max-w-4xl">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                        <MethodSelector method={method} setMethod={setMethod} />
                        <UploadForm
                            file={file}
                            loading={loading}
                            handleFileChange={handleFileChange}
                            handleSubmit={handleSubmit}
                            error={error}
                        />
                    </div>
                    {loading && <LoadingSpinner />}
                    {error && (
                        <div className="mt-8 p-6 rounded-xl bg-red-50 border-l-4 border-red-600 text-red-800 font-semibold shadow-md">
                            <p>{error}</p>
                        </div>
                    )}
                    {result && <ResultDisplay result={result} processingTime={processingTime} />}
                </div>
            </main>
            <Footer />
            <style>
                {`
                @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap');
                body {
                    font-family: 'Inter', sans-serif;
                }
                .animate-spin {
                    animation: spin 1s linear infinite;
                }
                @keyframes spin {
                    from { transform: rotate(0deg); }
                    to { transform: rotate(360deg); }
                }
                `}
            </style>
        </div>
    );
};

export default App;
